    /**
     * A regex for detecting URLs
     */
    public static final String weblinkRegex = "\\b(https?|ftp|file)://[-a-zA-Z0-9+&@#/%?=~_|!:,.;]*[-a-zA-Z0-9+&@#/%=~_|]";

    
    /**
     * A regex for detecting parentheses
     */
    public static final Pattern parensPattern = Pattern.compile("(\\(.*\\))");

    /**
     * A regex for detecting numbers
     */
    public static final Pattern numberPattern = Pattern.compile("\\b\\d+\\b");

    /**
     * A short list of some common suffixes
     */
    public static final ArrayList<String> suffixes = new ArrayList<String>() {
        {
            add("Jr.");
            add("II");
            add("III");
            add("IV");
            add("Esq");
            add("SJ");
            add("OP");
            add("OFM");
        }
    };
    
